    function getProData(data){
      var products =[];
   
      $.ajax({
                type: 'POST',
                url: 'http://localhost/ElectronicEcommerce/'+data.type,
                global: true,
                async: false,
                dataType: 'json',
                data:{data:data.id}
            })
                .done(function (response) {
                  if(response.length>0){
                    response.forEach(element => {
                      products.push({
                        pro_id:element.pro_id,
                        pro_name:element.pro_name,
                        pro_price:element.pro_price,
                        cat_name:element.cat_name,
                        main_img:element.main_img,
                        isInCart:inCart(element.pro_id,1,'cart'),
                        isInWishlist:inCart(element.pro_id,2,'wishlist'),
                        isInCompare:inCart(element.pro_id,3,'compare')
                      })
                    });
                  }
                })
                .fail(function () {
                })
                return products;
   }
    function inCart(id,type,localName) {
        var found = false;
        if (<?php echo isset($_SESSION['id']) ? 'true' : 'false'; ?>) {
            $.ajax({
                type: 'POST',
                url: 'http://localhost/ElectronicEcommerce/cart/getCurrentCart',
                global: true,
                async: false,
                dataType: 'json',
                data:{id:id,type:type}
            })
                .done(function (response) {
                  if(response[0] =='true'){
                        
                    found = '';
                  }
                  if(response[0] =='false'){
                    found='-outline'
                  }
                })
                .fail(function () {
                   // found = '-outline';

                })
        }
        else {
            const localItems = JSON.parse(localStorage.getItem(localName));
            if(localItems !== null){
            iteminCart = localItems.findIndex((x) => x.pro_id === id);
             if (iteminCart == -1) 
                found = '-outline';
                else
                  found = '';

            }
            else{
                found = '-outline';
            }

        }
        return found;
    }
